sqlMap.select:
select nvl(trunc(sum(a.prdQty*b.efp)/1000),0) "prevEfpSum", nvl(trunc(sum(a.prdQty*b.duty)/1000),0) "prevDutySum", nvl(trunc(sum(a.prdQty*b.cogs/1000)),0) "prevCogsSum" from gad01dt a inner join gme01dt b on a.prdcd = b.prdcd inner join gad01mt c on a.adsupportid = c.adsupportid and c.seq = b.seq where 1=1 and a.adSupportId = ( select MIN(adSupportId) KEEP(DENSE_RANK FIRST ORDER BY enddt desc) from gad01mt where endDt = ( select max(enddt) from gad01mt where venueCD = #venueCD# and apprStateCD = '50' and endDT < #startDT#
adSupportId != #adSupportID#
) and venueCD = #venueCD# and apprStateCD = '50' and endDT < #startDT#
adSupportId != #adSupportID#
) group by a.adSupportId

select nvl(trunc(apamt/1000), 0) as "prevAPamt", nvl(trunc(posmamt/1000), 0) as "prevPosmAmt", nvl(contractmonth, 0) as "prevContractMonth", nvl(trunc(incAmt/1000), 0) as "prevPayAmtSum" from gad01mt where 1=1 and adSupportId = ( select MIN(adSupportId) KEEP(DENSE_RANK FIRST ORDER BY enddt desc) from gad01mt where endDt = ( select max(enddt) from gad01mt where venueCD = #venueCD# and apprStateCD = '50' and endDT < #startDT#
adSupportId != #adSupportID#
) and venueCD = #venueCD# and apprStateCD = '50' and endDT < #startDT#
adSupportId != #adSupportID#
)

select sum(a.prdQty*b.eu) as "prevEuSum" from gad01dt a inner join gad01mt c on a.adSupportId = c.adSupportId inner join gme01dt b on a.prdcd = b.prdcd and b.seq = c.seq where 1=1 and a.adSupportId = ( select MIN(adSupportId) KEEP(DENSE_RANK FIRST ORDER BY enddt desc) from gad01mt where endDt = ( select max(enddt) from gad01mt where venueCD = #venueCD# and apprStateCD = '50' and endDT < #startDT#
adSupportId != #adSupportID#
) and venueCD = #venueCD# and apprStateCD = '50' and endDT < #startDT#
adSupportId != #adSupportID#
) group by a.adSupportId

select nvl(decode(b.prdQty, 0, 0, round(a.sdxPrdQty / b.prdQty * 100 , 1)), 0) as "prevSdxMixRate" from ( select sum(a.prdqty) as sdxPrdQty from gad01dt a inner join gad01mt b on a.adSupportId = b.adSupportId inner join gme01dt c on b.seq = c.seq and a.prdcd = c.prdcd where a.adSupportId = ( select MIN(adSupportId) KEEP(DENSE_RANK FIRST ORDER BY enddt desc) from gad01mt where endDt = ( select max(enddt) from gad01mt where venueCD = #venueCD# and apprStateCD = '50' and endDT < #startDT#
adSupportId != #adSupportID#
) and venueCD = #venueCD# and apprStateCD = '50' and endDT < #startDT#
adSupportId != #adSupportID#
) and c.sdxyn = 'Y') a, (select sum(a.prdqty) as prdQty from gad01dt a inner join gad01mt b on a.adSupportId = b.adSupportId where a.adSupportId = ( select MIN(adSupportId) KEEP(DENSE_RANK FIRST ORDER BY enddt desc) from gad01mt where endDt = ( select max(enddt) from gad01mt where venueCD = #venueCD# and apprStateCD = '50' and endDT < #startDT#
adSupportId != #adSupportID#
) and venueCD = #venueCD# and apprStateCD = '50' and endDT < #startDT#
adSupportId != #adSupportID#
) ) b

select t1.GSV as "compareGSV", NVL(DECODE(t1.venuedivcd, '10', DECODE(t1.subsegmentcd,'110304', t1.attrib02, t1.attrib01), t1.attrib01), 0) as "compareGSVRate", getLangDesc(#companyID#,#languageCD#,'F_CODE','TARGETRATE', '1', null) as "compareTargetRate", (select attrib01 from f_code where codeDiv = 'MINVOL' and comcode = t1.venueDivCD) as "compareMinVol", (select attrib02 from f_code where codeDiv = 'MINVOL' and comcode = t1.venueDivCD) as "limitGsv", (select attrib01 from f_code where codeDiv = 'COMPARECHGCOST' and comcode = t1.venueDivCD) as "compareChgCost", t1.venueDivCD as "venueDivCD" from (select sum(b.prdqty*c.efp) as GSV, d.subsegmentcd as subsegmentcd, d.venuedivcd as venuedivcd, NVL(e.attrib01,0) as attrib01, NVL(e.attrib02,0) as attrib02 from GAD01MT a INNER JOIN GAD01DT b on a.adsupportid = b.adsupportid INNER JOIN GME01DT c on a.seq = c.seq and b.prdCD = c.prdCD INNER JOIN GVEN01MT d on a.venueCD = d.venueCD INNER JOIN F_CODE e on e.codeDiv = 'VENUEDIVCD' and e.comCode = d.venueDivCD WHERE 1=1 and a.adSupportId = ( select MIN(adSupportId) KEEP(DENSE_RANK FIRST ORDER BY enddt desc) from gad01mt where endDt = ( select max(enddt) from gad01mt where venueCD = #venueCD# and apprStateCD = '50' and payStateCD = '10' and endDT < #startDT#
adSupportId != #adSupportID#
) and venueCD = #venueCD# and apprStateCD = '50' and payStateCD = '10' and endDT < #startDT#
adSupportId != #adSupportID#
) group by d.subsegmentcd,d.venuedivcd,e.attrib01,e.attrib02 )t1

select NVL(t.targetRate, -1) as "prevTargetRate" from(select MAX(DECODE(NVL(t1.targetrate, -1), 0, 0 , trunc((SELECT NVL(sum(a.inputqty * b.efp),0) as rltGSV FROM GRLT01DT a INNER JOIN GME01DT b ON a.prdCd = b.prdCd INNER JOIN GRLT01MT c ON a.venueCD = c.venueCD and a.eventYm = c.eventYm and c.verifyStateCD = 'OK' WHERE a.eventym between t1.startDT and t1.endDT and a.venuecd = #venueCD# and b.seq = ( SELECT seq FROM GAD01MT WHERE adSupportID = t1.adSupportID ) )/t1.targetrate*100,1)) ) KEEP(DENSE_RANK FIRST ORDER BY enddt desc) as targetRate from (select a.startDT as startDT, a.endDT as endDT, sum(b.prdQty*c.efp) as targetRate, a.adSupportID as adSupportID from GAD01MT a INNER JOIN GAD02DT b ON a.adSupportID = b.adSupportID INNER JOIN GME01DT c ON b.prdCD = c.prdCD and a.seq = c.seq WHERE 1=1 and a.adSupportId = ( select MIN(adSupportId) KEEP(DENSE_RANK FIRST ORDER BY enddt desc) from gad01mt where endDt = ( select max(enddt) from gad01mt where 1=1 and venueCD = #venueCD# and apprStateCD = '50' and payStateCD = '10' and endDT < #startDt#
adSupportId != #adSupportID#
) and venueCD = #venueCD# and apprStateCD = '50' and payStateCD = '10'
adSupportId != #adSupportID#
) and b.eventym between a.startDT and ( select MAX(eventYm) from grlt01mt WHERE eventym between a.startDT and a.endDT and venueCD = #venueCD# and verifyStateCD = 'OK' ) group by a.startDT, a.endDT, a.adSupportID ORDER BY a.startDT desc )t1 )t
